["custom"] = "function(event, arg1, arg2, arg3)\n    if (event == \"PLAYER_TARGET_CHANGED\") then\n        aura_env.wc_count = 0\n        local name, _, count = WA_GetUnitDebuff(\"target\", 228358, \"PLAYER\") -- Winter's Chill\n        if (name ~= nil) then\n            aura_env.wc_count = count\n            aura_env.wc_refreshed = false\n        end\n    end\n    if (event == \"UNIT_AURA\") then\n        local hasWC = false\n        local name = WA_GetUnitDebuff(\"target\", 228358, \"PLAYER\") -- Winter's Chill\n        if (name ~= nil) then\n            hasWC = true\n        end\n        if (hasWC == false) and (time() - aura_env.flurry_time > 2) then\n            aura_env.wc_count = 0\n        end\n    end\n    if (event == \"UNIT_SPELLCAST_SUCCEEDED\") then\n        local id = arg3\n        \n        if (id == 44614) then -- Flurry\n            --local name = WA_GetUnitBuff(\"player\", 190446)-- -- Brain Freeze\n            --if (name ~= nil) then--\n            aura_env.flurry_time = time()\n            aura_env.wc_count = 2\n            if (time() - aura_env.comet_storm_time < 2) then \n                aura_env.wc_count = 0\n            end\n            --end--\n        end\n        \n        if (id == 30455) or (id == 116) or (id == 199786) or (id == 157997) then -- Ice Lance / Frostbolt / Glacial Spike\n            local name, _, count = WA_GetUnitDebuff(\"target\", 228358, \"PLAYER\") -- Winter's Chill\n            count = count or 0\n            if(aura_env.wc_count > 0) then\n                aura_env.wc_count = aura_env.wc_count - 1\n                aura_env.wc_refreshed = false\n            end\n        end\n        \n        --[[ if (id == 153595) then -- Comet Storm\n            aura_env.wc_count = 0\n            aura_env.comet_storm_time = time()\n        end ]]--\n    end\n    \n    if (event == \"COMBAT_LOG_EVENT_UNFILTERED\") then\n        local sourceGUID, _, _, _, destGUID = select(4, CombatLogGetCurrentEventInfo())\n        local id = select(12, CombatLogGetCurrentEventInfo())\n        if(sourceGUID == UnitGUID(\"player\")  and destGUID == UnitGUID(\"target\")) then\n            if(id == 443934) then -- Splinterstorm Shatter (predictive)\n                aura_env.splinterstorm_time = time()\n            end\n            if(id == 443747) then -- Splinterstorm (applied per hit)\n                aura_env.wc_count = 2\n                aura_env.wc_refreshed = true\n            end\n            if (id == 228598) or (id == 228597) or (id == 228606) then -- Ice Lance / Frostbolt /  Glacial Spike\n                if(aura_env.wc_count > 0 and aura_env.wc_refreshed) then\n                    aura_env.wc_count = aura_env.wc_count - 1\n                    aura_env.wc_refreshed = false\n                end\n            end\n        end\n    end\n    \n    return true\nend",
