["custom"] = "function(allstates, e, ...)\n    if aura_env.show then\n        if e == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n            local sourceGUID = select(4, ...)\n            if sourceGUID == aura_env.playerGUID then\n                local subEvent = select(2, ...)\n                local destGUID = select(8, ...)\n                local spellID = select(12, ...)\n                local spellName = select(13, ...)\n                \n                if subEvent == \"SPELL_DAMAGE\" or subEvent == \"SPELL_ABSORBED\" then\n                    if aura_env.whiteList[spellID] then\n                        if not aura_env.damageLast or aura_env.damageLast < GetTime() - 0.2 then\n                            aura_env.damageLast = GetTime()\n                            \n                            aura_env.debug(subEvent .. \": \" .. spellName)\n                            \n                            aura_env.processCost(e, subEvent, aura_env.resourceCost(spellID))\n                        end\n                    end\n                end\n                \n                if subEvent == \"SPELL_CAST_SUCCESS\" then\n                    if aura_env.whiteListNoDamage[spellID] then\n                        aura_env.debug(subEvent .. \": \" .. spellName)\n                        \n                        aura_env.processCost(e, subEvent, aura_env.resourceCost(spellID))\n                    end\n                    \n                    if aura_env.whiteListPeriodic[spellID] then\n                        aura_env.channelStart = GetTime()\n                        aura_env.lastTick = aura_env.channelStart\n                        aura_env.periodicCount = 0\n                        aura_env.currentTarget = destGUID\n                        aura_env.channelHaste = UnitSpellHaste(\"player\") / 100\n                        \n                        aura_env.debug(\"Channel start at: \" .. aura_env.channelStart)\n                        aura_env.debug(subEvent .. \": \" .. spellName)\n                    end\n                end\n                if subEvent == \"SPELL_PERIODIC_DAMAGE\" or subEvent == \"SPELL_PERIODIC_HEAL\" or subEvent == \"SPELL_ABSORBED\" then\n                    if aura_env.currentTarget and not(aura_env.currentTarget == destGUID) then\n                        return true\n                    end\n                    if aura_env.whiteListPeriodic[spellID] then\n                        if not aura_env.lastPeriodic or aura_env.lastPeriodic < GetTime() - 0.01 then\n                            aura_env.lastPeriodic = GetTime()\n                            \n                            aura_env.periodicCount = aura_env.periodicCount + 1\n                            \n                            aura_env.debug(subEvent .. \": \" .. spellName)                        \n                            aura_env.debug(\"Tick: \" .. aura_env.periodicCount)\n                            aura_env.debug(\"Time since last tick: \" .. GetTime() - aura_env.lastTick)\n                            --aura_env.debug(\"Current energy: \" .. UnitPower(\"player\", 3))\n                            \n                            aura_env.lastTick = GetTime()\n                            \n                            aura_env.processCost(e, subEvent, aura_env.resourceCost(spellID))\n                        else\n                            print(\"skipped tick at \" .. GetTime())\n                        end\n                    end\n                end\n                \n                if subEvent == \"SPELL_MISSED\" then\n                    local missType = select(15, ...)\n                    if not missType == \"ABSORB\" then\n                        if aura_env.whiteList[spellID] then                            \n                            if not aura_env.missedLast or aura_env.missedLast < GetTime() - 0.2 then\n                                aura_env.missedLast = GetTime()\n                                \n                                aura_env.debug(subEvent .. \"(\" .. missType .. \"): \" .. spellName)\n                                \n                                aura_env.processCost(e, subEvent, aura_env.resourceCost(spellID))\n                            elseif aura_env.whiteListPeriodic[spellID] then\n                                aura_env.periodicMissed = true\n                            end\n                        end\n                    end\n                end\n                \n                if subEvent == \"SPELL_AURA_REMOVED\" and destGUID == aura_env.currentTarget then\n                    if aura_env.whiteListPeriodic[spellID] then\n                        if aura_env.periodicCount < 5 then\n                            \n                            aura_env.debug(subEvent .. \"(cancelled): \" .. spellName)\n                            aura_env.debug(\"Amount of ticks: \" .. aura_env.periodicCount)\n                            aura_env.debug(\"Channel time: \" .. GetTime() - aura_env.channelStart)\n                            aura_env.debug(\"Time since last tick: \" .. GetTime() - aura_env.lastTick)\n                            \n                            aura_env.processCost(event, subEvent, aura_env.cancelCost(spellID))\n                            aura_env.channelHaste = nil\n                        else\n                            aura_env.debug(subEvent .. \": \" .. spellName)\n                        end\n                    end\n                elseif subEvent == \"SPELL_AURA_REMOVED\" and destGUID == UnitGUID(\"player\") then\n                    if aura_env.whiteListPeriodic[spellID] and aura_env.periodicMissed then\n                        aura_env.periodicMissed = false\n                        \n                        local spellInfo = aura_env.channelInfo(spellID)\n                        local resourceCost = aura_env.resourceCost(spellID)\n                        local channeled = GetTime() - aura_env.channelStart\n                        local tickLength = spellInfo:tickLength()\n                        local ticks = math.floor(channeled / tickLength) + 1\n                        local totalCost = ticks * resourceCost\n                        totalCost = ticks < spellInfo.ticks and totalCost + aura_env.cancelCost(spellID) or totalCost\n                        \n                        aura_env.debug(subEvent .. \": \" .. spellName)\n                        \n                        aura_env.processCost(e, subEvent, totalCost)\n                        aura_env.channelHaste = nil\n                    end\n                end\n                \n                allstates[\"\"] = aura_env.defaultStateUpdate()\n                \n                return true \n            end\n        elseif e == \"TRIGGER\" then\n            allstates[\"\"] = aura_env.defaultStateUpdate()\n            \n            return true \n        end\n    end\nend",
