["custom"] = "aura_env.region.configGroup = \"flurry_strikes_bar\"\naura_env.saved = aura_env.saved or {}\naura_env.saved.spent = aura_env.saved.spent or 0\naura_env.saved.adjust = {}\naura_env.saved.adjust.min = 0\naura_env.saved.adjust.mid = 0\naura_env.saved.adjust.max = 0\naura_env.i = 0\naura_env.fsThrottle = 3\naura_env.fsDuration = 1.5\naura_env.resetValue = 300\naura_env.playerGUID = UnitGUID(\"player\")\naura_env.currentSpec = GetSpecialization()\n\naura_env.customGlobals = {}\naura_env.customGlobals.title = WrapTextInColorCode(aura_env.id, \"ffE6CC80\")\n\naura_env.resourceCost = function(id)\n    local costInfo = C_Spell.GetSpellPowerCost(id)\n    if costInfo then\n        for i = 1, #costInfo do\n            if ((costInfo[i].requiredAuraID ~= 0 and costInfo[i].hasRequiredAura) or costInfo[i].requiredAuraID == 0) and costInfo[i].type == 3 then\n                local spellCost = costInfo[i].cost\n                return spellCost\n            end\n        end\n    end\n    return\"notFound\"\nend\n\naura_env.processCost = function(event, subEvent, cost)\n    aura_env.debug(\"Counter incremented: \" .. cost)\n    aura_env.debug(\"Timestamp: \" .. GetTime())\n    \n    aura_env.lastCost = cost\n    \n    aura_env.saved.spent = aura_env.saved.spent + cost\n    aura_env.requestReset({event = event, subEvent = subEvent})\nend\n\naura_env.cancelCost = function(spellID)\n    --aura_env.debug(\"Haste on channel start: \" .. aura_env.channelHaste)\n    local tickLength = aura_env.channelInfo[spellID]:tickLength()\n    --aura_env.debug(\"Tick length: \" .. tickLength)\n    local timeSinceLast = GetTime() - aura_env.lastTick\n    local partialTick = timeSinceLast / tickLength\n    partialTick = partialTick > 1 and 1 or partialTick\n    --aura_env.debug(\"Partial tick(preround): \" .. partialTick)\n    local precision = 0.2\n    partialTick = partialTick % precision < precision * 0.5 and (partialTick - (partialTick % precision)) or (partialTick - (partialTick % (precision * -1)))\n    --aura_env.debug(\"Partial tick: \" .. partialTick)\n    local cancelCost = math.floor((aura_env.resourceCost(spellID) * partialTick) + 0.5)\n    return cancelCost\nend\n\naura_env.hastedDuration = function(base)\n    local hastePercent = UnitSpellHaste(\"player\")\n    local baseDuration = base\n    \n    local hastedDuration = baseDuration / (1 + hastePercent / 100)\n    \n    return hastedDuration\nend\n\naura_env.requestReset = function(params)\n    params = params or {}\n    if params.hard then\n        aura_env.saved.spent = 0\n        aura_env.saved.adjust.min = 0\n        aura_env.saved.adjust.max = 0\n        aura_env.saved.adjust.mid = 0\n        aura_env.printResetInfo(aura_env.saved.spent, params.event, params.subEvent)\n        return\n        \n    elseif params.fs and aura_env.config.smartResync then\n        \n        aura_env.lastCost = aura_env.lastCost<15 and aura_env.lastCost+20 or aura_env.lastCost\n        -- Unexpected resets\n        if aura_env.saved.spent > aura_env.lastCost then\n            \n            -- If our min and max range doesn't cover a reset, set min and max according to cost\n            if (((aura_env.saved.spent - aura_env.lastCost - aura_env.saved.adjust.mid + aura_env.saved.adjust.min) > 0) and ((aura_env.saved.spent - aura_env.saved.adjust.mid + aura_env.saved.adjust.max) <= aura_env.resetValue)) or (aura_env.saved.adjust.max < aura_env.saved.adjust.min) then\n                aura_env.saved.adjust.min = 1\n                aura_env.saved.adjust.max = aura_env.lastCost\n                aura_env.saved.adjust.mid = math.floor((aura_env.saved.adjust.max + aura_env.saved.adjust.min)/2)\n                aura_env.saved.spent = aura_env.saved.adjust.mid\n                \n                -- Subsequent unexpected resets, check if early or late reset  \n            else\n                \n                -- Early reset means increase min to match resetValue\n                if (aura_env.saved.spent + aura_env.saved.adjust.max - aura_env.saved.adjust.mid) > aura_env.resetValue then\n                    aura_env.saved.adjust.min = aura_env.saved.adjust.mid + aura_env.resetValue - aura_env.saved.spent + 1\n                    \n                    -- And decrease max if last ability would have reset at max\n                    if (aura_env.saved.spent - aura_env.lastCost - aura_env.saved.adjust.mid + aura_env.saved.adjust.max) > aura_env.resetValue then\n                        aura_env.saved.adjust.max = aura_env.resetValue - (aura_env.saved.spent - aura_env.lastCost - aura_env.saved.adjust.mid)\n                    end\n                    \n                    -- Late reset means decrease max\n                else\n                    aura_env.saved.adjust.max = aura_env.saved.adjust.mid - (aura_env.saved.spent - aura_env.lastCost)\n                    \n                    -- And increase min if min wouldn't have procced\n                    if (aura_env.saved.spent + aura_env.saved.adjust.min - aura_env.saved.adjust.mid) <= 0 then\n                        aura_env.saved.adjust.min = aura_env.saved.adjust.mid - aura_env.saved.spent + 1\n                    end\n                end\n                \n                local nmid = math.floor((aura_env.saved.adjust.max + aura_env.saved.adjust.min)/2)\n                aura_env.saved.spent = aura_env.saved.spent + nmid - aura_env.saved.adjust.mid\n                aura_env.saved.adjust.mid = nmid\n                \n            end\n            if aura_env.saved.spent > aura_env.resetValue then\n                aura_env.saved.spent = aura_env.saved.spent - aura_env.resetValue\n                aura_env.printResetInfo(aura_env.saved.spent, params.event, params.subEvent)\n            end\n            \n            \n            -- Expected resets\n        elseif not (aura_env.saved.adjust.min == aura_env.saved.adjust.max) then\n            \n            -- Check if max is too high and would have procced earlier\n            if (aura_env.saved.adjust.max - aura_env.saved.adjust.mid) > (aura_env.lastCost - aura_env.saved.spent) then\n                aura_env.saved.adjust.max = aura_env.saved.adjust.mid + aura_env.lastCost - aura_env.saved.spent\n                \n            end\n            \n            -- Check if min is too low and wouldn't have procced\n            if (aura_env.saved.adjust.mid - aura_env.saved.adjust.min) >= (aura_env.saved.spent) then\n                aura_env.saved.adjust.min = aura_env.saved.adjust.mid - aura_env.saved.spent + 1\n            end\n            local nmid = math.floor((aura_env.saved.adjust.max + aura_env.saved.adjust.min)/2)\n            aura_env.saved.spent = aura_env.saved.spent + nmid - aura_env.saved.adjust.mid\n            aura_env.saved.adjust.mid = nmid\n            \n            if aura_env.saved.spent > aura_env.resetValue then\n                aura_env.saved.spent = aura_env.saved.spent - aura_env.resetValue\n                aura_env.printResetInfo(aura_env.saved.spent, params.event, params.subEvent)\n            end\n            \n        end\n        return\n        \n    elseif aura_env.saved.spent > aura_env.resetValue then\n        if not aura_env.last or aura_env.last < GetTime() - aura_env.fsThrottle then\n            aura_env.last = GetTime()\n            \n            aura_env.saved.spent = aura_env.saved.spent - aura_env.resetValue\n            aura_env.printResetInfo(aura_env.saved.spent, params.event, params.subEvent)\n            return\n        end\n    end\nend\n\naura_env.defaultStateUpdate = function()\n    return {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = aura_env.saved.spent,\n        total = aura_env.resetValue,\n    }\nend\n\naura_env.debug = function(msg)\n    if aura_env.config.verbose then\n        print(aura_env.customGlobals.title .. \" \" .. msg)\n    end\nend\n\naura_env.printResetInfo = function(currentPool, event, subEvent)\n    if aura_env.config.verboseReset then\n        local text = \"Reset to \" .. currentPool\n        if event then\n            text = text .. \" triggered by \" .. event\n            if subEvent then\n                text = text .. \" - \" .. subEvent\n                if type then\n                    text = text .. \" - \" .. type\n                end\n            end\n        end\n        print(aura_env.customGlobals.title .. \" \" .. text)\n    end\nend\n\naura_env.whiteList = {\n    [100780] = true, -- Tiger Palm\n    [121253] = true, -- Keg Smash\n}\n\naura_env.whiteListPeriodic = {\n    [117952] = true, -- Crackling Jade Lightning\n}\n\naura_env.whiteListNoDamage = {\n    [115078] = true, -- Paralysis\n    [116095] = true, -- Disable\n}\n\naura_env.channelInfo = {\n    [117952] = { -- Crackling Jade Lightning\n        base = function() return IsPlayerSpell(459809) and 2 or 4 end,\n        --base = function() return 4 end,\n        hasted = function(self) return self.base() / (1 + (aura_env.channelHaste and aura_env.channelHaste or UnitSpellHaste(\"player\") / 100)) end,\n        ticks = 5,\n        tickLength = function(self) return self:hasted() / (self.ticks - 1) end\n    }\n}",
