["init"] = {
["custom"] = "aura_env.firstRunTextPrefix = '' -- the text that will prefix the count on the firstRun - e.g \"\" or \"?\"\n\naura_env.spellIds = {\n    fullMoon  =  274283;\n    shootingStars =  202497;\n    crashingStar = 408310;\n    convoke = 391528;\n    convokeDF = 12345\n}\n\naura_env.procCount = 25\n\naura_env.UAPB_OB_DELAY = 0.1 -- delay before checking if hardcast or proc. 0.1 should be a reasonable value\n\n\nif not aura_env.saved then\n    aura_env.saved = {}\nend\n\nif not aura_env.saved.stacks then\n    aura_env.saved.stacks = 0\nend\n\nif not aura_env.saved.firstRun then\n    aura_env.saved.firstRun = true\nend\n\naura_env.setStacks = function(s)\n    s = s % aura_env.procCount\n    aura_env.saved.stacks = s\n    \n    WeakAuras.ScanEvents(\"UAPB_OB_STACKS\", s)\n    \n    return s\nend\n\naura_env.handleLogin = function(state)\n    \n    state.stacks = aura_env.setStacks(0)\n    state.value = state.stacks\n    state.changed = true\n    \n    return state\nend\n\naura_env.handleSpellDamage = function(state)\n    \n    state.stacks = aura_env.setStacks(state.stacks + 1)\n    state.value = state.stacks\n    state.text = state.stacks\n    state.changed = true    \n    \n    return state\nend\n\naura_env.handleSpellChannelStart = function(state, spellId)\n    \n    if spellId == aura_env.spellIds.convoke then\n        state.convoke = true\n    end \n    \nend\n\naura_env.handleSpellChannelStop = function(state, spellId)\n    \n    if spellId == aura_env.spellIds.convoke then\n        C_Timer.After(aura_env.UAPB_OB_DELAY, function()\n                state.convoke = false\n        end)\n    end\n    \nend\n\n\naura_env.handleEnergized = function(state)\n    \n    state.energized = state.energized + 1\n    state.changed = true\n    \n    -- send delayed event since this event fires for both proc and cast\n    C_Timer.After(aura_env.UAPB_OB_DELAY, function()\n            WeakAuras.ScanEvents(\"UAPB_OB_DELAY\", aura_env.id)  \n    end)\n    \n    return state\nend\n\naura_env.handleSpellCastSuccess = function(state)\n    \n    state.cast = true -- full moon was hardcast\n    state.changed = true\n    \n    return state\nend\n\n\naura_env.handleDelay = function(state)\n    \n    if aura_env.stacksShouldReset(state) then\n        \n        state.stacks = aura_env.resetStacks(state)\n        state.value = state.stacks\n        state.text = state.stacks\n        state.firstRun = false\n        \n    else   \n        \n        state.cast = false -- this was a hard cast full moon, reset the value\n    end\n    \n    state.energized = state.energized - 1        \n    state.changed = true\n    \n    return state\nend\n\n\naura_env.stacksShouldReset = function(state)\n    \n    return not state.convoke             -- we cant differentiate OB proc or convoke proc\n    and (not state.cast and state.energized == 1) -- proc\n    or ( state.cast and state.energized == 2) -- proc, then full moon cast within delay time\nend\n\n\naura_env.resetStacks = function(state)\n    return aura_env.setStacks(0)\nend",
["do_custom"] = true,
},
["finish"] = {
