{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "event",
["unit"] = "player",
["customDuration"] = "function()\n    return 10, aura_env.expires\nend",
["customName"] = "function()\n    if not aura_env.useShortNumbers then\n        return aura_env.curValue\n    end\n    \n    local value = aura_env.curValue\n    if value >= 1000000 then\n        value = (math.floor(value / 10000) / 100) .. \"m\"\n    elseif value >= 1000 then\n        value = math.floor(value / 1000) .. \"k\"\n    end\n    return value\nend",
["custom"] = "function(event, ...)\n    local _, message = ...\n    local destName = select(9, ...)\n    if destName and UnitIsUnit(\"player\", destName) then\n        if message == \"SPELL_ABSORBED\" then\n            local chk = select(12, ...) -- thanks to Skada\n            local spellID, absorbed\n            if type(chk) == \"number\" then -- Spell\n                spellID = select(19, ...)\n                absorbed = select(22, ...)\n            else -- Swing\n                spellID = select(16, ...)\n                absorbed = select(19, ...)\n            end\n            \n            if spellID == 122470 then\n                aura_env.curValue = math.max(aura_env.curValue - absorbed, 0)\n                -- we don't get AURA_REMOVED until 10 seconds\n                -- after AURA_APPLIED, so might as well force this\n                if aura_env.curValue == 0 then\n                    aura_env.isKarmaOn = false\n                end\n                \n                return aura_env.isKarmaOn\n            end\n            \n        elseif message == \"SPELL_AURA_APPLIED\" then\n            local spellID = select(12, ...)\n            \n            if spellID == 122470 then\n                aura_env.maxValue = math.floor(UnitHealthMax(\"player\") * 0.5)\n                aura_env.curValue = aura_env.maxValue\n                aura_env.expires = GetTime() + 10\n                aura_env.isKarmaOn = true\n                return true\n            end\n        elseif message == \"SPELL_AURA_REMOVED\" then\n            local spellID = select(12, ...)\n            if spellID == 122470 then\n                aura_env.isKarmaOn = false\n                return false\n            end\n        end        \n    end\n    return false\nend",
